import axios from "axios";
import React, {useState, useEffect} from "react";
import styles from './Package.scss';


const Package = () => {
    const [data, setData] = useState(false)
    const [userRole, setUserRole] = useState('user')
    const [showEdit, setShowEdit] = useState({})
    const [editView, isEditView] = useState(false)
    useEffect(() =>{
      setUserRole(localStorage.getItem('userRole'))
      loadData()
    }, [])

const loadData = async () => {
    console.log('loadData')

    await axios.get('http://localhost:8000/package/getall')
    .then((res) => {
        setDatas(res.data)
    })
    .catch((err) => console.log('>>>>err', err))
}

const setDatas = async (data) => {
        setData(data)
        console.log('data>>28>', data)
}

const viewEdit = async (data) => {
  setShowEdit(data)
  isEditView(true)
  console.log('viewEdit>>>>>34', data, 'editView', editView)
}


const viewEditClose = async () => {
  isEditView(false)
}

const viewEditSubmit = async () => {
  console.log('loadData')

  await axios.post('http://localhost:8000/package/update')
  .then((res) => {
      setDatas(res.data)
  })
  .catch((err) => console.log('>>>>err', err))
}

return (

  <div>
      {/* editView */}
      { editView ? 
      <div className="editView">
      <button onClick={() => viewEditClose()}>Close</button>
        <div className="editZone">
          <input type="text" name="name" placeholder={showEdit.name} /><br/>
          <input type="text" name="info" placeholder={showEdit.info} /><br/>
          <input type="text" name="prices" placeholder={showEdit.prices} /><br/>
          <input type="text" name="size" placeholder={showEdit.size} />
        </div>
      <button onClick={() => viewEditSubmit()}>Edit</button>

      </div> : <></> }
      {/* HTML */}
      <br></br>
    <table class="table">
      <thead>
        <tr> <br></br>
          <th>Package Name</th> 
          <th>Package info</th> 
          <th>Prices</th> 
        </tr>
      </thead>
      <tbody>
        {
          data ? data.map((item, index) =>
          <tr>
            <td>{index + 1}</td>
            <td>{item.name}</td>
            <td>{item.info}</td>
            <td>{item.prices}</td>
            <td> 
            { userRole === 'admin' ?  <button onClick={() => viewEdit(item)}>Edit</button> : <></> }
            </td>
          </tr>
            )
            :null
        }
  </tbody>
    </table> 
    <div>
    { userRole === 'admin' ?  <button >Add Package</button> : <></> }

    </div>

  </div>
)

}

export default Package;
