import axios from "axios";
import React, {useState, useEffect} from "react";
import Typography from '@mui/material/Typography';

//import styles from './Package.scss';


const Wax = () => {
    const [data, setData] = useState(false)
    const [userRole, setUserRole] = useState('user')
    const [showEdit, setShowEdit] = useState({})
    const [editView, isEditView] = useState(false)
    const [addView, isAddView] = useState(false)
    const [form, setForm] = useState({});
    const handleChange = (e) => {
      setForm({
        ...form,
        [e.target.name]: e.target.value,
      });
      console.log('form', form)
    };
    useEffect(() =>{
      setUserRole(localStorage.getItem('userRole'))
      loadData()
    }, [])

const loadData = async () => {
    console.log('loadData')

    await axios.get('http://localhost:8000/wax/getall')
    .then((res) => {
        setDatas(res.data)
    })
    .catch((err) => console.log('>>>>err', err))
}

const setDatas = async (data) => {
        setData(data)
        console.log('data>>28>', data)
}

const viewEdit = async (data) => {
  setShowEdit(data)
  isEditView(true)
  console.log('viewEdit>>>>>34', data, 'editView', editView)
}


const viewEditClose = async () => {
  isEditView(false)
}

const viewEditSubmit = async (data) => {
  form._id = showEdit._id
  console.log('loadData>>>', form)
  await axios.post('http://localhost:8000/wax/update', form)
  .then((res) => {
    loadData()
  })
  .catch((err) => console.log('>>>>err', err))
}

const viewAdd = async (data) => {
  setShowEdit(data)
  isAddView(true)
  console.log('viewEdit>>>>>34', data, 'editView', editView)
}


const viewAddClose = async () => {
  isAddView(false)
}

const viewAddSubmit = async (data) => {
  console.log('73loadData>>>', form)
  await axios.post('http://localhost:8000/wax', form)
  .then((res) => {
    loadData()
  })
  .catch((err) => console.log('>>>>err', err))
}

return (

  <div>
      {/* editView */}
      { editView ? 
      <div className="editView">
      <button onClick={() => viewEditClose()}>Close</button>
        <div className="editZone">
          <input type="text" name="name" placeholder={showEdit.name} id="name" onChange={handleChange} /> <br/>
          <input type="text" name="serial" placeholder={showEdit.serial} id="serial" onChange={handleChange}/> <br/>
          <input type="text" name="volume" placeholder={showEdit.volume} id="volume" onChange={handleChange} /> <br/>
          <input type="text" name="prices" placeholder={showEdit.prices} id="prices" onChange={handleChange}/> <br/>
          <input type="text" name="amount" placeholder={showEdit.amount} id="amount" onChange={handleChange}/> <br/>
        </div>
      <button onClick={() => viewEditSubmit()}>Edit</button>

      </div> : <></> }

      { addView ? 
      <div className="addView">
      <button onClick={() => viewAddClose()}>Close</button>
        <div className="addZone">
          <input type="text" name="name" placeholder="name" id="package" onChange={handleChange}/> <br/>
          <input type="text" name="serial" placeholder="serial" id="serial" onChange={handleChange} /> <br/>
          <input type="text" name="volume" placeholder="volume" id="volume" onChange={handleChange} /> <br/>
          <input type="text" name="prices" placeholder="prices" id="prices" onChange={handleChange} /> <br/>
          <input type="text" name="amount" placeholder="amount" id="amount" onChange={handleChange} /> <br/>
        </div>
      <button onClick={() => viewAddSubmit()}>Add</button>

      </div> : <></> }

      {/* HTML */}
      <Typography component="h1" variant="h7">
            Car Wax
      </Typography>

    <table className="customers">
      <thead>
        <tr> <th>No.</th>
          <th>Wax Name</th> 
          <th>Serial</th> 
          <th>Volume</th> 
          <th>Prices</th> 
          <th>Amount</th>
        </tr>
      </thead>
        {
          data.length > 0 ? data.map((item, index) =>
      <tbody key={item._id}>
          <tr>
            <td>{index + 1}</td>
            <td>{item.name}</td>
            <td>{item.serial}</td>
            <td>{item.volume}</td>
            <td>{item.prices}</td>
            <td>{item.amount}</td>
            <td> 
            { userRole === 'admin' ?  <button onClick={() => viewEdit(item)}>Edit</button> : <></> }
            </td>
          </tr>
  </tbody>

            )
            :null
        }
    </table> 
      <div>
      { userRole === 'admin' ?  <button onClick={() => viewAdd()}>Add Wax</button> : <></> } <br/>
      { userRole === 'admin' ?  <a className="btn btn-sm btn-outline-secondary" href="/adminpage">Back</a> : <></> }
      { userRole === 'user' ?  <a className="btn btn-sm btn-outline-secondary" href="/userpage">Back</a> : <></> }
      </div>
  </div>
)

}

export default Wax;
